import React from 'react';
import './Button.css';

const Button = (props) => {
    return (
        <button className={`
        button
        ${props.operation ? 'operation' : ''}
        ${props.double ? 'double' : ''}
        ${props.triple ? 'triple' : ''}
        `}
        onClick={e =>props.click(e.target.innerHTML)}>
            {props.label}
        </button>
    );
}

export default Button;
